import { Component, ViewChild, ElementRef, AfterViewInit } from '@angular/core';
import { Router } from '@angular/router';
import { Usuario } from 'src/app/models/usuario';
import { UsuarioService } from 'src/app/services/usuario.service';
import { Modal } from 'bootstrap';
import { UsuarioDataServiceService } from 'src/app/services/usuario-data-service.service';
import { Tipoid } from 'src/app/models/tipoid';
import { Plan } from 'src/app/models/plan';

@Component({
  selector: 'app-usuarios',
  templateUrl: './usuarios.component.html',
  styleUrls: ['./usuarios.component.css']
})
export class UsuariosComponent implements AfterViewInit {

  usuarios: Usuario[];
  usuario: Usuario;

  @ViewChild('myModal') myModal: ElementRef;
  modalInstance: any;

  constructor(private service: UsuarioService, private router: Router, private usuarioDataService: UsuarioDataServiceService){}

  ngOnInit(): void {
    this.getUsuarios();

    this.service.usuarioChanged.subscribe(() => {
      // Recargamos la lista de usuarios cada vez que se crea o actualiza un usuario.
      this.getUsuarios();
    });
  }

  private getUsuarios(): void {
    this.service.getUsuarios().subscribe(usuarios => {
      this.usuarios = usuarios;
    });
  }

  ngAfterViewInit() {
    this.modalInstance = new Modal(this.myModal.nativeElement);
  }

  mostrarFormulario(id: number) {
    this.service.viewUsuario(id).subscribe(usuario => {
      this.usuarioDataService.changeUsuario(usuario);
      this.modalInstance.show();
    });
  }

  // Puedes llamar a este método en caso de que necesites ocultar el modal.
  ocultarFormulario() {
    this.modalInstance.hide();
  }

  initUsuario() {
    const newUser = new Usuario();
    newUser.tipoidentificacion = new Tipoid();
    newUser.plan = new Plan();
    this.usuarioDataService.changeUsuario(newUser);
  }

  deleteUsuario(id: number)
  {

    if (confirm('¿Estás seguro de que quieres eliminar este usuario?')) {
      this.service.deleteUsuario(id).subscribe(() => {
        // Elimina el usuario de la lista local de usuarios después de que la eliminación sea exitosa.
        this.usuarios = this.usuarios.filter(usuario => usuario.id !== id);
      }, error => {
        console.error('Error eliminando usuario', error);
      });
    }

  }
}
<br />
<button
  type="button"
  class="btn btn-secondary sm"
  data-bs-toggle="modal"
  data-bs-target="#exampleModal"
  (click)="initUsuario()"
>
  Agregar Cliente
</button>

<br />
<br />
<div class="card my-6">
  <div class="card-header">Lista de usuarios</div>
  <div class="card-body">
    <table class="table table-hover table-striped" *ngIf="usuarios?.length > 0">
      <thead>
        <tr>
          <th>id</th>
          <th>Documento</th>
          <th>Fecha Nacimiento</th>
          <th>Primer Nombre</th>
          <th>Segundo Nombre</th>
          <th>Primer Apellido</th>
          <th>Segundo Apellido</th>
          <th>Direccion</th>
          <th>Celular</th>
          <th>Email</th>
          <th>Acciones</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let usuario of usuarios"
        (click)="mostrarFormulario(usuario.id)"
        style="cursor: pointer" >
          <td>{{ usuario.id }}</td>
          <td>{{ usuario.numerodocumento }}</td>
          <td>{{ usuario.fechanacimiento | date : "dd-MM-yyyy" }}</td>
          <td>{{ usuario.primernombre | uppercase }}</td>
          <td>{{ usuario.segundonombre | uppercase }}</td>
          <td>{{ usuario.primerapellido | uppercase }}</td>
          <td>{{ usuario.segundoapellido | uppercase }}</td>
          <td>{{ usuario.direccionresidencia }}</td>
          <td>{{ usuario.numerocelular }}</td>
          <td>{{ usuario.email }}</td>
          <td>
            <button
              type="button"
              name="eliminar"
              id="eliminar"
              (click)="deleteUsuario(usuario.id); $event.stopPropagation();"
              class="btn btn-secondary sm"
            >
              Eliminar
            </button>
          </td>
        </tr>
      </tbody>
    </table>
  </div>
</div>

<!-- Modal -->
<div
  #myModal
  class="modal modal-xl fade"
  id="exampleModal"
  data-bs-backdrop="static"
  tabindex="-1"
  aria-labelledby="exampleModalLabel"
  aria-hidden="true"
>
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h1 class="modal-title fs-5" id="exampleModalLabel">
          Registrar Clientes
        </h1>
        <button
          type="button"
          class="btn-close"
          data-bs-dismiss="modal"
          aria-label="Close"
        ></button>
      </div>
      <div class="modal-body">
        <form>
          <div class="container">
            <div class="col-12 form-text">
              <div class="row">
                <app-usuario-new></app-usuario-new>
              </div>
            </div>
          </div>
        </form>
      </div>
    </div>
  </div>
</div>
